import React, { useState, useEffect } from "react";
import axios from 'axios';
import Sectiontitle from "../components/Sectiontitle";
import Layout from "../components/Layout";
import Pagination from "../components/Pagination";
import PortfoliosView from "../components/PortfoliosView";

const metaData = {
    title: `Jason Pierce's Portfolio`,
    description: `Check out some of my completed (or ongoing) work from various projects`,
    keywords: 'Jason Pierce, Portfolio, Examples'
};

const PortfolioPage = () => {
    const [portfolios, setPortfoios] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [portfoliosPerPage] = useState(9);

    useEffect(() => {
      axios.get('/api/portfolios')
        .then( response => {
          setPortfoios(response.data);
        })
    }, [portfolios]);

    const indexOfLastPortfolios = currentPage * portfoliosPerPage;
    const indexOfFirstPortfolios = indexOfLastPortfolios - portfoliosPerPage;
    const currentPortfolios = portfolios.slice(indexOfFirstPortfolios, indexOfLastPortfolios);

    const paginate = (e, pageNumber) => {
      e.preventDefault();
      setCurrentPage(pageNumber);
    }

    return (
      <Layout pageMetaData={metaData}>
        <div className="mi-about mi-section mi-padding-top mi-padding-bottom">
          <div className="container">
            <Sectiontitle title="Portfolios" />
            <PortfoliosView portfolios={currentPortfolios}/>
            {!(portfolios.length > portfoliosPerPage) ? null : (
              <Pagination 
                className="mt-50"
                itemsPerPage={portfoliosPerPage}
                totalItems={portfolios.length}
                paginate={paginate}
                currentPage={currentPage}
              />
            )}
          </div>
        </div>
      </Layout>
    );
};

export default PortfolioPage;
